import React, { useState, useEffect } from "react"
import api from './api'

const App = () => {
  const [transactions, setTransactions] = useState([]);
  const [formData, setFormData] = useState({
    label: '',
    desc: ''
  });

  const fetchLabel = async () => {
    const response = await api.get('/get_label/');
    setTransactions(response.data)
  };

  useEffect(() => {
    // fetchLabel();
  }, []);

  const handleInputChange = (event) => {
    setFormData({
      ...formData
    });
  };

  const handleFormSubmit = async (event) => {
    event.preventDefault();
    await api.post('/label/', formData);
    fetchLabel();
    setFormData({
      lable: '',
      desc: ''
    });
  };

  return (
    <div class="mb-3">
      <nav className='navbar navbar-dark bg-primary'>
        <div className='container-fluid'>
          <a className='navbar-brand' href="#">
            PLVB App
          </a>
        </div>
      </nav>
      <form>
        <p class="text-start">Đăng nhập</p>
        <div class="mb-2 row">
          <label for="inputText" class="col-sm-1   col-form-label border">Username</label>
          <div class="col-sm-2">
            <input type="text" class="form-control" placeholder="Username" id="inputUsername" ></input>
          </div>
        </div>
        <div class="mb-2 row">
          <label for="inputPassword" class="col-sm-1 col-form-label border">Password</label>
          <div class="col-sm-2">
            <input type="password" class="form-control" id="inputPassword"></input>
          </div>
        </div>
        <input class="btn btn-primary w-100" type="submit" value="Login"></input>
      </form>
      <div class="container mt-5">
        <div class="row">
          <div class="col-md-6">
            <div class="bg-light p-4">
              <div class="mb-3">
                <button class="btn btn-primary w-100">Button</button>
              </div>
              <p>This is some content in the parent div.</p>
            </div>
          </div>
        </div>
      </div>
    </div>


  )

}

export default App;
