import React, {useState, useEffect} from "react"
import api from './api'

const App = () => {
  const [transactions, setTransactions] = useState([]);
  const [formData, setFormData] = useState({
    label:'',
    desc:''
  });

  const fetchLabel = async () => {
    const response = await api.get('/get_label/');
    setTransactions(response.data)
  };

  useEffect(() => {
    fetchLabel();
  }, []);

  const handleInputChange = (event) => {
    setFormData({
      ...formData
    });
  };

  const handleFormSubmit = async (event) => {
    event.preventDefault();
    await api.post('/label/', formData);
    fetchLabel();
    setFormData({
      lable:'',
      desc:''
    });
  };

  return (
    <div>
      <nav className='navbar navbar-dark bg-primary'>
        <div className='container-fluid'>
          <a className='navbar-brand' href="#">
            PLVB App
          </a>
        </div>
      </nav>
      <div class="input-group flex-nowrap">
        <span class="input-text" id="addon-wrapping">Username</span>
        <input type="text" class="form-control" placeholder="Username" aria-label="Username" aria-describedby="addon-wrapping"></input>
      </div>
    </div>
  )

}

export default App;
