import React, { useState, useEffect } from "react"
import api from './api'

const App = () => {
  const [transactions, setTransactions] = useState([]);
  const [formData, setFormData] = useState({
    label: '',
    desc: ''
  });

  const fetchLabel = async () => {
    const response = await api.get('/get_label/');
    setTransactions(response.data)
  };

  useEffect(() => {
    // fetchLabel();
  }, []);

  const handleInputChange = (event) => {
    setFormData({
      ...formData
    });
  };


  const handleFormSubmit = async (event) => {
    event.preventDefault();
    await api.post('/auth/token', formData);
    fetchLabel();
    setFormData({
      username: '',
      password: ''
    });
  };

  return (
    <div class="mb-3">
      <nav className='navbar navbar-dark bg-primary'>
        <div className='container-fluid'>
          <a className='navbar-brand' href="#">
            PLVB App
          </a>
        </div>
      </nav>
      <form onSubmit={handleFormSubmit}>
        <p class="text-start">Đăng nhập</p>
        <div class="mb-2 row">
          <label for="inputText" class="col-sm-1   col-form-label border">Username</label>
          <div class="col-sm-2">
            <input type="text" class="form-control" placeholder="Username" id="inputUsername" onChange={handleInputChange} value={formData.username}></input>
          </div>
        </div>
        <div class="mb-2 row">
          <label for="inputPassword" class="col-sm-1 col-form-label border">Password</label>
          <div class="col-sm-2">
            <input type="password" class="form-control" id="inputPassword" onChange={handleInputChange} value={formData.password}></input>
          </div>
        </div>
        <input class="btn btn-primary w-30" type="submit" value="Login"></input>
      </form>

    </div>
  )

}

export default App;
